package com.wedding.user.service.impl;import com.wedding.user.dao.UserAccountDao;import com.wedding.user.dao.UsersDao;import com.wedding.user.dto.UsersDto;import com.wedding.user.pojo.Users;import com.wedding.user.service.IUsersService;import org.springframework.beans.BeanUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * Created by chuweifeng on 14-9-15. */@Servicepublic class UsersServiceImpl implements IUsersService{    @Autowired    private UsersDao usersDao;    @Override    public int insertUsers(UsersDto usersDto) {        Users users = new Users();        BeanUtils.copyProperties(usersDto, users);        return usersDao.insert(users);    }    @Override    public int updateUsers(UsersDto usersDto) {        Users users = new Users();        BeanUtils.copyProperties(usersDto, users);        return usersDao.updateByPrimaryKey(users);    }    @Override    public UsersDto selectUsersByUserId(String userAccountId) {        Users users = usersDao.selectUsersByUserId(userAccountId);        if(users!=null){            UsersDto usersDto = new UsersDto();            BeanUtils.copyProperties(users, usersDto);            return usersDto;        }else{            return null;        }    }}