package com.wedding.user.service.impl;import com.wedding.user.dao.UsersSiteDao;import com.wedding.user.dto.UsersDto;import com.wedding.user.dto.UsersSiteDto;import com.wedding.user.pojo.UserSite;import com.wedding.user.pojo.Users;import com.wedding.user.service.IUsersSiteService;import org.springframework.beans.BeanUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * Created by chuweifeng on 14-9-15. */@Servicepublic class UsersSiteServiceImpl implements IUsersSiteService {    @Autowired    private UsersSiteDao usersSiteDao;    @Override    public int insertUsersSite(UsersSiteDto usersSiteDto) {        UserSite usersSite = new UserSite();        BeanUtils.copyProperties(usersSiteDto, usersSite);        return usersSiteDao.insert(usersSite);    }    @Override    public int updateUsersSite(UsersSiteDto usersSiteDto) {        UserSite usersSite = new UserSite();        BeanUtils.copyProperties(usersSiteDto, usersSite);        return usersSiteDao.updateByPrimaryKey(usersSite);    }    @Override    public UsersSiteDto selectUsersSiteById(String siteId) {        UserSite userSite = usersSiteDao.selectUsersSiteById(siteId);        if(userSite!=null){            UsersSiteDto usersSiteDto = new UsersSiteDto();            BeanUtils.copyProperties(userSite, usersSiteDto);            return usersSiteDto;        }else{            return null;        }    }}